INSERT INTO `CPSV-AP`.`EVENT` (`ID`,`IDENTIFIER_CONTENT`,`IDENTIFIER_LANGUAGE`,`NAME_CONTENT`,`NAME_LANGUAGE`,`DESCRIPTION_CONTENT`,`DESCRIPTION_LANGUAGE`,`PUBLIC_SERVICE_ID`) VALUES (11, '', 'DK', 'Having a child', 'EN', '<p>This life event groups public services related to becoming a caretaker for a child, for instance in case of giving birth, adopting, receiving a foster child&hellip;</p>\r\n', 'CY', NULL),(12, '', 'DK', 'Becoming a (social) caretaker', 'EN', '<p>This life event groups public services related to the situation where you need to take care of another person (other than when you get a child), for instance for an elder, a disabled person&hellip;</p>\r\n', 'EN', NULL),(13, '', '', 'Starting business', 'EN', '', '', NULL),(14, '', '', 'Starting education', 'EN', '<p>This life event groups any public service related to education, for example pre-school education, elementary school, higher education and university&hellip;</p>\r\n', '', NULL),(15, '', '', 'Looking for a new job', 'EN', '<p>This life event groups public services for when someone looks for a new job or starts a new job.</p>\r\n', '', NULL),(16, '', '', 'Losing/quitting a job This life event groups public services related', 'EN', '<p>This life event groups public services related to the situation when someone leaves or quits a particular jobs on his own, or when someone loses his job, for instance getting fired, collective dismissal, in case of failure of the company&hellip;</p>\r\n', '', NULL),(17, '', '', 'Looking for a place to live', 'EN', '<p>This life event groups public services related to a person&rsquo;s place of living, for instance, changing residence, buying a house or a piece of land, building, renting a house or apartment&hellip;</p>\r\n', '', NULL),(18, '', '', 'Changing relationship status', 'EN', '<p>This life event groups public services related to a person&rsquo;s official relationship, for instance marriage, registered partnership, divorce&hellip;</p>\r\n', 'EN', NULL),(19, '', '', 'Driving a vehicle', 'EN', '<p>This life event groups public services related to driving a vehicle, for instance car, motorcycle&hellip; Some example public services are getting your driver license, following driving lessons, registering your car&hellip;</p>\r\n', 'EN', NULL),(20, '', '', 'Registering a company', 'EN', '<p>This business event groups public services that are related to different kind of activities that have to be done before the business is eligible to operate, for instance registering in the business register, registering as a VAT payer, notifying the residence of your company&hellip;</p>\r\n', 'EN', NULL),(21, '', '', 'Moving to/from the country', 'EN', '<p>This life event groups public services that relate to when someone moves from one country to another.</p>\r\n', 'EN', NULL),(22, '', '', 'Needing a licence, permit or certificate to start or continue an activity', 'EN', '<p>This business events groups public services related to the request of a licence, permit, certificate or other official document that have to be acquired before starting to execute a certain activity.</p>\r\n', 'EN', NULL),(23, '', 'EN', 'Notifying and reporting to authorities', 'EN', '', '', NULL);
